import { NavLink } from 'react-router-dom';

const navStyle = {
  display: 'flex',
  borderBottom: '1px solid #ccc',
  padding: '0.5rem',
};

const linkStyle = {
  padding: '0.5rem 1rem',
  textDecoration: 'none',
  color: 'black',
};

const activeStyle = {
  fontWeight: 'bold',
  borderBottom: '2px solid blue',
};

function Navigation() {
  return (
    <div style={navStyle}>
      <NavLink to="/" style={linkStyle} end activeStyle={activeStyle}>
        Email Compiler
      </NavLink>
      <NavLink to="/signatures" style={linkStyle} activeStyle={activeStyle}>
        Signatures
      </NavLink>
      <NavLink to="/templates" style={linkStyle} activeStyle={activeStyle}>
        Templates
      </NavLink>
    </div>
  );
}

export default Navigation;
