import React from 'react';
import styles from './TemplateCard.module.css';

interface TemplateCardProps {
  name: string;
  content: string;
  last_modified: string;
  isFavourite: boolean;
  toggleFavourite: (name: string) => void;
}

const TemplateCard: React.FC<TemplateCardProps> = ({
  name,
  content,
  last_modified,
  isFavourite,
  toggleFavourite,
}) => {
  return (
    <div className={styles.card}>
      <div className={styles.header}>
        {/* TODO: Make it so Jason erases the favourites if they're remove instead of making an infinite list */}
        <svg
          className={styles.star}
          onClick={() => toggleFavourite(name)}
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill={isFavourite ? 'gold' : 'grey'}
          width="24px"
          height="24px"
        >
          <path d="M12.9,2.6l2.3,5c0.1,0.3,0.4,0.5,0.7,0.6l5.2,0.8C22,9,22.3,10,21.7,10.6l-3.8,3.9c-0.2,0.2-0.3,0.6-0.3,0.9
            l0.9,5.4c0.1,0.8-0.7,1.5-1.4,1.1l-4.7-2.6c-0.3-0.2-0.6-0.2-0.9,0l-4.7,2.6c-0.7,0.4-1.6-0.2-1.4-1.1l0.9-5.4
            c0.1-0.3-0.1-0.7-0.3-0.9l-3.8-3.9C1.7,10,2,9,2.8,8.9l5.2-0.8c0.3,0,0.6-0.3,0.7-0.6l2.3-5C11.5,1.8,12.5,1.8,12.9,2.6z"/>
        </svg>

        <div className={styles.title}>{name}</div>
      </div>
      <div className={styles.meta}>Last modified: {last_modified}</div>
      <div className={styles.content}>{content}</div>
    </div>
  );
};

export default TemplateCard;
