import React, { useEffect, useState } from 'react';
import { core } from '@tauri-apps/api';

import TemplateCard from '../TemplateCard/TemplateCard';
import styles from './TemplateList.module.css';

type TemplateFile = {
  name: string;
  content: string;
  last_modified: string;
};

const TemplateList: React.FC = () => {
  const [templates, setTemplates] = useState<TemplateFile[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [favourites, setFavourites] = useState<Record<string, boolean>>({});

  useEffect(() => {
    core.invoke<TemplateFile[]>('load_templates')
      .then(setTemplates)
      .catch((err) => {
        console.error(err);
        setError('Failed to load templates');
      });

    core.invoke<Record<string, boolean>>('load_favourites')
      .then(setFavourites)
      .catch((err) => {
        console.error(err);
        setError('Failed to load favourites');
      });
  }, []);

    // It toggles the favourite status of a file
    const toggleFavourite = (filename: string) => {
      const current = !!favourites[filename]; // fallback: treat undefined as false
      const updated = { ...favourites, [filename]: !current }; // flip the value
      setFavourites(updated); // update UI
      core.invoke('save_favourites', { favourites: updated }); // save to JSON
    };


return (
  <div className={styles.container}>
    <h2 className={styles.heading}>Loaded Templates</h2>
    {error && <div className={styles.error}>{error}</div>}
    {templates.map((template) => (
      <TemplateCard
        key={template.name}
        name={template.name}
        content={template.content}
        last_modified={template.last_modified}
        isFavourite={!!favourites[template.name]}
        toggleFavourite={toggleFavourite}
      />
    ))}
  </div>
);

};

export default TemplateList;
