import React, { useState } from 'react';

export default function SignaturesView() {
  const [name, setName] = useState('');
  const [position, setPosition] = useState('');

  return (
    <div style={{ display: 'flex', height: '100%', padding: '1rem' }}>
      {/* Left column: placeholder for signature list */}
      <div style={{ width: '15%', borderRight: '1px solid #ccc', paddingRight: '1rem' }}>
        <h3>Saved Signatures</h3>
        {/* Signature list will go here */}
      </div>

      {/* Right column: form */}
      <div style={{ width: '85%', paddingLeft: '1rem' }}>
        <h3>Create or Edit Signature</h3>
        <form>
          <div style={{ marginBottom: '1rem' }}>
            <label>Name:</label><br />
            <input
              type="text"
              value={name}
              onChange={e => setName(e.target.value)}
              style={{ width: '100%' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label>Position:</label><br />
            <input
              type="text"
              value={position}
              onChange={e => setPosition(e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
        </form>
      </div>
    </div>
  );
}
