import React, { useCallback, useEffect, useState } from 'react';
import { core } from '@tauri-apps/api';
import clipboard from 'tauri-plugin-clipboard-api';

import SignatureCard from '../components/SignatureCard/SignatureCard';

export default function SignaturesView() {

  const [signatureName, setSignatureName] = useState('');
  const [name, setName] = useState('');
  const [position, setPosition] = useState('');
  const [department, setDepartment] = useState('');
  const [company, setCompany] = useState('');
  const [locations, setLocations] = useState<Record<string, string>>({});
  const [selectedLocation, setSelectedLocation] = useState('');
  const [imageFileName, setImageFileName] = useState('');
  const [signatureList, setSignatureList] = useState<
    { signatureName: string; userName: string }[]
  >([]);
  const [viewMode, setViewMode] = useState<'preview' | 'edit'>('preview');
  const [lastSelectedSignature, setLastSelectedSignature] = useState<string | null>(null);
  const [imageBase64, setImageBase64] = useState<string | null>(null);

  interface Signature {
  signature_name: string;
  name: string;
  position: string;
  department: string;
  company: string;
  location: {
    name: string;
    address: string;
  };
  image_filename: string;
  }

  const handleSave = () => {
    const signature = {
      signature_name: signatureName,
      name,
      position,
      department,
      company,
      location: {
        name: selectedLocation,
        address: locations[selectedLocation] ?? ''
      },
      image_filename: imageFileName
    };

    core.invoke('save_signature_to_file', { signature })
      .then(() => { 
        console.log('Signature saved to file!')
        reloadSignatureList();
      })
      .catch(err => console.error('Failed to save signature:', err));
  };

  const reloadSignatureList = () => {
    core.invoke<string[]>('load_signatures')
      .then((names) => {
        Promise.all(
          names.map((name) =>
            core.invoke<Signature>('load_signature_file', { name })
              .then((sig) => ({
                signatureName: sig.signature_name,
                userName: sig.name,
              }))
          )
        ).then(setSignatureList);
      })
      .catch((err) => console.error('Failed to reload signatures:', err));
  };

  const handleSelectSignature = useCallback((name: string, mode: 'preview' | 'edit' = 'preview') => {
    core.invoke<Signature>('load_signature_file', { name })
      .then((sig) => {
        setSignatureName(sig.signature_name);
        setName(sig.name);
        setPosition(sig.position);
        setDepartment(sig.department);
        setCompany(sig.company);
        setSelectedLocation(sig.location.name);
        setImageFileName(sig.image_filename);
        setViewMode(mode);
        setLastSelectedSignature(name);
        
        if (name !== lastSelectedSignature) {
          core.invoke('save_cache', {
            cache: { last_selected_signature: name }
          }).catch((err) => console.error('Failed to save cache:', err));
        }

        if (sig.image_filename) {
                core.invoke<string>('get_signature_image_base64', { filename: sig.image_filename })
                  .then(setImageBase64)
                  .catch((err) => console.error('Failed to load image:', err));
              } else {
                setImageBase64(null); // Clear if no image
              }
            })
            .catch((err) => console.error('Failed to load signature:', err));
  }, [lastSelectedSignature]);

  const handleDeleteSignature = (name: string) => {
    core.invoke('delete_signature_file', { name })
      .then(() => {    
        setSignatureList((prev) => prev.filter((sig) => sig.signatureName !== name));
      })
      .catch((err) => console.error('Failed to delete signature:', err));
  };

  const handleCopyToClipboard = async () => {
    const html = `
      <div>
        <strong>${name}</strong><br/>
        ${position}, ${department}<br/>
        ${company}<br/>
        ${locations[selectedLocation] ?? ''}<br/>
        ${
          imageBase64
            ? `<img src="data:image/png;base64,${imageBase64}" style="max-width:300px;margin-top:1rem;" />`
            : ''
        }
      </div>
    `;
    try {
      await clipboard.writeHtml(html);
      //await clipboard.writeText(`${name}\n${position}, ${department}\n${company}`);
      console.log('Copied to clipboard!');
    } catch (err) {
      console.error('Failed to copy to clipboard:', err);
    }
  };


  useEffect(() => {
    core.invoke<{ last_selected_signature: string | null }>('load_cache')
      .then((cache) => {
        if (cache.last_selected_signature) {
          setLastSelectedSignature(cache.last_selected_signature);
        }
      })
      .catch((err) => console.error('Failed to load cache:', err));
  }, []);

  useEffect(() => {
    core.invoke<string[]>('load_signatures')
      .then((names) => {
        Promise.all(
          names.map((name) =>
            core.invoke<Signature>('load_signature_file', { name })
              .then((sig) => ({
                signatureName: sig.signature_name,
                userName: sig.name,
              }))
          )
        ).then((list) => {
          setSignatureList(list);

          // Load cache and select signature
          core.invoke<{ last_selected_signature: string | null }>('load_cache')
            .then((cache) => {
              const fallback = list[0]?.signatureName;
              const toSelect = cache.last_selected_signature ?? fallback;
              if (toSelect && viewMode === 'preview') 
                {
                  handleSelectSignature(toSelect, 'preview');
                }
            })
            .catch((err) => console.error('Failed to load cache:', err));
        });
      })
      .catch((err) => console.error('Failed to load signatures:', err));
  }, [handleSelectSignature, viewMode]);


  useEffect(() => {
    core.invoke<Record<string, string>>('load_locations')
      .then(setLocations)
      .catch(err => console.error('Failed to load locations:', err));
  }, []);
  
  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImageFileName(file.name);
    }
  };

  return (
    <div style={{ display: 'flex', height: '100%', padding: '1rem' }}>
      {/* Left column: placeholder for signature list */}
      <div style={{ width: '20%', borderRight: '1px solid #ccc', paddingRight: '1rem' }}>      
        {signatureList.map(({ signatureName, userName }) => (
          <SignatureCard
            key={signatureName}
            signatureName={signatureName}
            userName={userName}
            onSelect={() => handleSelectSignature(signatureName, 'preview')}
            onEdit={() => handleSelectSignature(signatureName, 'edit')}
            onDelete={() => handleDeleteSignature(signatureName)}
          />
        ))}
      </div>
     
      {/* Right column: 80% width */}      
      { viewMode === 'preview' ? (
        <div>
          <h3>Signature Preview</h3>
          <p><strong>{name}</strong>
          <br/>{position}, {department}
          <br/>{company}
          <br/>{locations[selectedLocation]}</p>          
          { imageBase64 && (
            <img
              src={`data:image/png;base64,${imageBase64}`}
              alt="Signature"
              style={{ maxWidth: '300px', marginTop: '1rem' }}
            />
          )}
          <div style={{ marginTop: '1rem' }}>
            <button onClick={() => setViewMode('edit')}>Edit</button>
            <button onClick={() => handleCopyToClipboard()}>Copy to Clipboard</button>
          </div>
        </div>
      ) : (     // Edit View
        <div style={{ width: '80%', paddingLeft: '1rem' }}>
          <h3>Create or Edit Signature</h3>
          <form onSubmit={e => e.preventDefault()}>
            {/* Signature Name */}
            <div style={{ marginBottom: '1rem' }}>
              <label>Signature Name:</label><br />
              <input
                type="text"
                value={signatureName}
                onChange={e => setSignatureName(e.target.value)}
                style={{ width: '100%' }}
              />
            </div>

            {/* Name and Position */}
            <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ flex: 1 }}>
                <label>Name<span style={{ color: 'red' }}> *</span>:</label><br />
                <input
                  type="text"
                  value={name}
                  onChange={e => setName(e.target.value)}
                  style={{ width: '100%' }}
                />
              </div>
              <div style={{ flex: 1 }}>
                <label>Position<span style={{ color: 'red' }}> *</span>:</label><br />
                <input
                  type="text"
                  value={position}
                  onChange={e => setPosition(e.target.value)}
                  style={{ width: '100%' }}
                />
              </div>
            </div>

            {/* Company and Department */}
            <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ flex: 1 }}>
                <label>Company:</label><br />
                <input
                  type="text"
                  value={company}
                  onChange={e => setCompany(e.target.value)}
                  style={{ width: '100%' }}
                />
              </div>
              <div style={{ flex: 1 }}>
                <label>Department:</label><br />
                <input
                  type="text"
                  value={department}
                  onChange={e => setDepartment(e.target.value)}
                  style={{ width: '100%' }}
                />
              </div>
            </div>

            {/* Location Dropdown and File Picker */}
            <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
              <div style={{ flex: 1 }}>
                <label>Location:</label><br />
                <select
                  value={selectedLocation}
                  onChange={e => setSelectedLocation(e.target.value)}
                  style={{ width: '100%' }}
                >
                  <option disabled value="">-- Select Location --</option>
                  {Object.entries(locations).map(([name, address]) => (
                    <option key={name} value={name}>
                      {name}
                    </option>
                  ))}
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label>Company Logo or Signature Image:</label><br />
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ width: '100%' }}
                />
              </div>
            </div>

            {/* Save Button */}
            <div>
              <button type="button" onClick={handleSave}>Save</button>
            </div>
          </form>
        </div>
      )}
    </div>  
  );
}
