// components/SignatureCard/SignatureCard.tsx
import React, { useState } from 'react';
import styles from './SignatureCard.module.css';

interface SignatureCardProps {
  signatureName: string;
  userName: string;
  lastModified?: string;
  onEdit: () => void;
  onDelete: () => void;
  onSelect: () => void;
}

const SignatureCard: React.FC<SignatureCardProps> = ({
  signatureName,
  userName,
  lastModified,
  onEdit,
  onDelete,
  onSelect,
}) => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <div className={styles.card} onClick={onSelect}>
      <div className={styles.left}>
        <div className={styles.title}>{signatureName}</div>
        <div className={styles.subtitle}>{userName}</div>
      </div>
      <div className={styles.right}>
        {lastModified && <div className={styles.date}>{lastModified}</div>}
        <div
          className={styles.menuIcon}
          onClick={(e) => {
            e.stopPropagation();
            setMenuOpen(!menuOpen);
          }}
        >
          {/* 3-dot SVG */}
          <svg width="24" height="24" viewBox="0 0 16 16" fill="currentColor">
            <path d="M9.5 13a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm0-5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm0-5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0z" />
          </svg>
        </div>
        {menuOpen && (
          <div className={styles.menu}>
            <button onClick={onEdit}>
              {/* Edit SVG */}
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <path d="M5 19h1.425L16.2 9.225L14.775 7.8L5 17.575zm-2 2v-4.25L16.2 3.575q.3-.275.663-.425t.762-.15t.775.15t.65.45L20.425 5q.3.275.438.65T21 6.4q0 .4-.137.763t-.438.662L7.25 21zM19 6.4L17.6 5zm-3.525 2.125l-.7-.725L16.2 9.225z" />
              </svg>
              Edit
            </button>
            <button onClick={onDelete}>
              {/* Trash SVG */}
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <path d="M7 21q...z" />
              </svg>
              Delete
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default SignatureCard;
